DROP TABLE IF EXISTS departments CASCADE;
CREATE TABLE "departments" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "departmentId" int,
  "title" varchar,
  "description" varchar,
  "created_at" timestamp
);

