DROP TABLE IF EXISTS employmentTypes CASCADE;

CREATE TABLE "employmentType" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "TypeId" int,
  "employment" varchar,
  "created_at" timestamp
);

