CREATE TABLE "partTime" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "ParttimeID" int,
  "hourlyRate" int,
  "created_at" timestamp
);

CREATE TABLE "fullTime" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "fulltimeID" int,
  "Salary" int,
  "created_at" timestamp
);

CREATE TABLE "leaves" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "LeaveId" int,
  "leaveNum" int,
  "position" varchar,
  "created_at" timestamp
);

CREATE TABLE "Training" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "trainingId" int,
  "Type" varchar,
  "number" int,
  "position" varchar,
  "created_at" timestamp
);

CREATE TABLE "Attendance" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "AttendanceId" int,
  "position" varchar,
  "Number" int,
  "Date" int,
  "created_at" timestamp
);

ALTER TABLE "departments" ADD FOREIGN KEY ("departmentId") REFERENCES "users" ("id");

ALTER TABLE "positions" ADD FOREIGN KEY ("postId") REFERENCES "departments" ("id");

ALTER TABLE "employmentType" ADD FOREIGN KEY ("TypeId") REFERENCES "positions" ("id");

ALTER TABLE "partTime" ADD FOREIGN KEY ("ParttimeID") REFERENCES "employmentType" ("id");

ALTER TABLE "fullTime" ADD FOREIGN KEY ("fulltimeID") REFERENCES "employmentType" ("id");

ALTER TABLE "departments" ADD FOREIGN KEY ("id") REFERENCES "leaves" ("LeaveId");

ALTER TABLE "departments" ADD FOREIGN KEY ("id") REFERENCES "Training" ("trainingId");

ALTER TABLE "departments" ADD FOREIGN KEY ("id") REFERENCES "Attendance" ("AttendanceId");
